import React, { useState } from "react";

const Form = ({ addTodo }) => {
    const [inputValue, setInputValue] = useState("");

    const handleInputChange = (e) => {
        setInputValue(e.target.value);
    };

    const handleFormSubmit = (e) => {
        e.preventDefault();

        if(inputValue.trim() === "") return;

        addTodo({ title: inputValue, completed: false });
        setInputValue("");
    };

    return (
        <form className="ui form" onSubmit={handleFormSubmit}>
            <div className="ui grid center aligned">
                <div className="row">
                    <div className="column five wide">
                        <input
                            value={inputValue}
                            onChange={handleInputChange}
                            type="text"
                            placeholder="Ingresar Tarea a Realizar"
                        />
                    </div>
                    <div className="column four wide">
                        <div className="column one wide">
                            <button type="submit" className="ui circular inverted olive button">
                                <i className="white plus icon"></i> Add Tacks
                            </button> 

                            <button  className="ui circular inverted teal button"> 
                                <i className="white github icon"></i>Repositorio
                            </button>
                        </div>
                        </div>
                </div>
            </div>
        </form>
    );
};

export default Form;
